export interface Country {
    id: number;
    title: CountryTitle;
    capital: string;
    code: CountryCode;
    continent: string;
    flag: string;
    largeFlag: string;
    unicode: string;
    emoji: string;
    dialCode: CountryDialCode;
    iso: boolean;
    active: boolean;
    timezone: string;
    currency: CountryCurrencyCode | null;
    currencySymbol: CountryCurrencySymbol | null;
    timezones: Array<string>;
    name: CountryName;
    currencyData: Array<CountryCurrencyData>;
    currencies: CountryCurrency;
    nativeNames: Array<CountryNativeNames>;
    timezones: Array<string>;
    currencyName?: string;
}

export type CountryMinimal = Pick<
    Country,
    | 'id'
    | 'title'
    | 'code'
    | 'timezone'
    | 'dialCode'
    | 'currency'
    | 'currencySymbol'
    | 'capital'
    | 'continent'
    | 'emoji'
    | 'active'
    | 'unicode'
>;

export type CountryKeys = keyof Country;
export type CountryMinimalKeys = keyof CountryMinimal;

export type CountryCurrency = Partial<
    Record<
        CountryCurrencyCode,
        {
            name: string;
            symbol: string;
        }
    >
>;

export interface CountryName {
    official: string;
    common: string;
    nativeName: CountryNativeName;
}

export interface CountryNativeNames {
    code: CountryNativeNameCode;
    official: string;
    common: string;
}

export type CountryNativeName = Partial<
    Record<
        CountryNativeNameCode,
        {
            official: string;
            common: string;
        }
    >
>;

export type CountryCurrencies = Record<
    string,
    {
        name: string;
        symbol: string;
    }
>;

export interface CountryCurrencyData {
    name: string;
    symbol: string;
    code: CountryCurrencyCode;
}

export type MyCountryFlag = {
    img: string;
    emoji: string;
    emoji_unicode: string;
};

export type MyCountryConnection = {
    asn: number;
    org: string;
    isp: string;
    domain: 'earth.net.bd' | string;
};

export type MyCountryTimezone = {
    id: string;
    abbr: string;
    is_dst: boolean;
    offset: number;
    utc: string;
    current_time: string;
};

export type MyCountry = {
    About_Us: 'https://ipwhois.io' | string;
    ip: string;
    success: boolean;
    type: string;
    continent: string;
    continent_code: string;
    country: string;
    country_code: CountryCode;
    region: string;
    region_code: string;
    city: string;
    latitude: number;
    longitude: number;
    is_eu: boolean;
    postal: string;
    calling_code: string;
    capital: string;
    borders: string;
    flag: MyCountryFlag;
    connection: MyCountryConnection;
    timezone: MyCountryTimezone;
};

